export const initialState = {
    basket: []
};

// Selector
export const getBasketTotal = (basket) =>
 basket?.reduce((amount, item) => item.price + amount, 0); I

const reducer = (state, action) => {
    console.log(action);
    switch (action.type) {
      case "ADD_TO_BASKET":
         return {
           ...state,
           basket: [...state.basket, action.item],
        };

        default: 
            return state;
    }
};

export default reducer;
